  "Resource": "arn:aws:states:::eks:runJob",
  "Parameters": {
    "ClusterName": "spark-k8s-monitor",
    "CertificateAuthority": "",
    "Endpoint": "https://62021BE274D5057D49FE8494441EA0F0.gr7.us-west-2.eks.amazonaws.com",
    "Job": {
      "apiVersion": "batch/v1",
      "kind": "Job",
      "metadata": {
        "generateName": "my-spark-job-"
      },
      "spec": {
        "template": {
          "metadata": {
            "name": "my-spark-job"
          },
          "spec": {
            "containers": [
              {
                "name": "spark",
                "image": "gcr.io/spark-operator/spark-py:v3.1.1",
